// src/App.js
import React, { useState } from 'react';

// Component imports
import TopBar from './components/Dashboard/TopBar';
import LoginPage from './components/Auth/Login';
import SignupPage from './components/Auth/Signup'; // Make sure this component exists
import Dashboard from './components/Dashboard/Dashboard';

// Optional: Remove or replace with actual CSS file
// import './styles/global.css'; 

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeTab, setActiveTab] = useState('login');

  const handleLogin = () => {
    setIsLoggedIn(true);
    setActiveTab('dashboard');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setActiveTab('login');
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif",
      WebkitFontSmoothing: 'antialiased',
      MozOsxFontSmoothing: 'grayscale',
      color: '#1f2937'
    }}>
      {/* Top Navigation Bar */}
      <div style={{
        background: 'linear-gradient(to right, #3b82f6, #6366f1)',
        color: 'white',
        padding: '16px 24px',
        boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          maxWidth: '1200px',
          margin: '0 auto'
        }}>
          <div style={{ textAlign: 'center' }}>
            <h1 style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>Alladin's Chirag</h1>
            <p style={{ fontSize: '0.875rem', opacity: 0.9 }}>Kalagi Group of Companies</p>
          </div>
          {isLoggedIn && (
            <div style={{ display: 'flex', gap: '12px' }}>
              <button style={{
                display: 'flex',
                alignItems: 'center',
                gap: '4px',
                background: 'rgba(255, 255, 255, 0.2)',
                padding: '8px 16px',
                borderRadius: '9999px',
                border: 'none',
                color: 'white',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}>
                Profile
              </button>
              <button style={{
                display: 'flex',
                alignItems: 'center',
                gap: '4px',
                background: 'rgba(255, 255, 255, 0.2)',
                padding: '8px 16px',
                borderRadius: '9999px',
                border: 'none',
                color: 'white',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}>
                Settings
              </button>
              <button 
                onClick={handleLogout}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '4px',
                  background: '#ef4444',
                  padding: '8px 16px',
                  borderRadius: '9999px',
                  border: 'none',
                  color: 'white',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Main Content Area */}
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '24px'
      }}>
        {!isLoggedIn && activeTab === 'login' && (
          <LoginPage onLogin={handleLogin} onSwitchToSignup={() => setActiveTab('signup')} />
        )}
        {!isLoggedIn && activeTab === 'signup' && (
          <SignupPage onSignup={handleLogin} onSwitchToLogin={() => setActiveTab('login')} />
        )}
        {isLoggedIn && activeTab === 'dashboard' && <Dashboard />}
      </div>
    </div>
  );
}

export default App;
