// src/App.js
import React, { useState } from 'react';
import TopBar from './components/Dashboard/TopBar';
import LoginPage from './components/Auth/Login';
import SignupPage from './components/Auth/Signup'; // Make sure this exists
import Dashboard from './components/Dashboard/Dashboard';

export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeTab, setActiveTab] = useState('login');

  const handleLogin = () => {
    setIsLoggedIn(true);
    setActiveTab('dashboard');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setActiveTab('login');
  };

  const handleSwitchToSignup = () => {
    setActiveTab('signup');
  };

  const handleSwitchToLogin = () => {
    setActiveTab('login');
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #145ceeff 0%, #b42663ff 100%)',
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif",
      WebkitFontSmoothing: 'antialiased',
      MozOsxFontSmoothing: 'grayscale',
      color: '#1f2937'
    }}>
      {/* Top Navigation Bar */}
      <TopBar isLoggedIn={isLoggedIn} onLogout={handleLogout} />

      {/* Main Content Area */}
      <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '24px' }}>
        {!isLoggedIn && activeTab === 'login' && (
          <LoginPage onLogin={handleLogin} onSwitchToSignup={handleSwitchToSignup} />
        )}
        {!isLoggedIn && activeTab === 'signup' && (
          <SignupPage onSignup={handleLogin} onSwitchToLogin={handleSwitchToLogin} />
        )}
        {isLoggedIn && activeTab === 'dashboard' && <Dashboard />}
      </div>
    </div>
  );
}
